mapper.select:
SELECT sno, title, category, posted_time, hits, writer, filename FROM (SELECT sno, title, category, posted_time, hits, writer, filename, CEIL(rownum/10) AS page FROM (SELECT sno, title, category, to_char(posted_time, 'YYYY.MM.DD') AS posted_time, hits, writer, filename FROM sharetips
category = #{category}
ORDER BY sno DESC)) WHERE page=#{pageNo}

SELECT id
FROM recommend_sharetips
WHERE sno = #{value}

SELECT sno, title, category, posted_time, hits, writer, filename FROM (SELECT sno, title, category, posted_time, hits, writer, filename, CEIL(rownum/10) AS page FROM (SELECT sno, title, category, to_char(posted_time, 'YYYY.MM.DD') AS posted_time, hits, writer, filename FROM sharetips
category LIKE #{category} AND
content LIKE '%' || #{word} || '%'
writer LIKE #{word}
title LIKE '%' || #{word} || '%'
ORDER BY sno DESC)) WHERE page = #{pageNo}

mapper.update:
UPDATE sharetips
set title=#{title}, content=#{content}, filename=#{fileName} ,category=#{category}
where sno=#{sno}

UPDATE sharetips
SET hits = hits + 1
WHERE sno=#{value}

mapper.select:
SELECT sno, title, category, posted_time, hits, writer, filename FROM (SELECT sno, title, category, posted_time, hits, writer, filename, CEIL(rownum/10) AS page FROM (SELECT sno, title, category, to_char(posted_time,'YYYY.MM.DD') as posted_time, hits, writer, filename FROM sharetips
writer = #{id}
ORDER BY sno DESC)) WHERE page=#{pageNo}

SELECT sno FROM (SELECT sno, CEIL(rownum/10) AS page FROM (SELECT sno FROM recommend_sharetips
id = #{id}
ORDER BY sno DESC)) WHERE page=#{pageNo}

SELECT * FROM( SELECT lead(sno, 2) over (order by sno asc) as bbsno, lead(sno, 1) over (order by sno asc) as bsno, sno, lag(sno, 2) over (order by sno asc) as asno, lag(sno, 1) over (order by sno asc) as aasno FROM ( SELECT * FROM sharetips ORDER BY sno asc)
category = #{category}
ORDER BY sno DESC ) WHERE sno = #{sno}
